<?phpclass WebUser extends CWebUser {    private $_model = null;    private $_EnrollNumber = null;        public function getRole() {         if ($user = $this->getModel()) {                      return $user->Role;        }    }    public function getUserData() {        return Member::model()->findByPk($this->id);    }        public function getEnrollNumber()    {        if($this->isGuest)        {            return false;        }        if($this->_EnrollNumber === null)        {            $this->_EnrollNumber = $this->getUserData()->EnrollNumber;        }        return $this->_EnrollNumber;    }    private function getModel() {        if (!$this->isGuest && $this->_model === null) {             $this->_model = Member::model()->findByPk($this->id, array('select' => 'Role'));        }                        return $this->_model;    }        public function init()    {        $settings = array(            'httpOnly' => true        );        $this->identityCookie=$settings;        parent::init();    }}